INSERT INTO TBL_ASSEDIO VALUES (29,'24.10.2013','Estava voltando para casa no ônibus 4111 quando um homem começou a se masturbar na fila do outro lado do ônibus. Eu fiquei muito nervosa, mas não sabia o que fazer. Ele trocou de lugar e veio para o assento atrás de mim. Saí dali e fui para um assento do lado da trocadora do ônibus. Assim que saí do ônibus, encarei o rapaz (que tinha uns 20 e poucos anos) e ele deu um sorrisinho. Não sei se fui a única que percebi, mas fiquei com medo de, se eu fizesse algo, ele pudesse revidar.',24,FALSE,'23.05.2018','Belo Horizonte','Minas Gerais',18,2,7,2,1,ST_GeomFromText('POINT(-43.9896 -19.9256)', 4326));
