INSERT INTO TBL_ASSEDIO VALUES (40,'15.01.2014','Estava na praia pela manhã, no posto onde frequento desde de criança, era dia de semana por isso a praia estava bem vazia, algumas famílias estavam a minha volta mas não muito próximas. Estava sentada na cadeira de praia e de olhos fechados descansando, quando percebo que tem alguma sombra em cima de mim e abro os olhos imaginando que uma nuvem estava passando pelo sol naquele momento, quando vejo é um garoto que deve ter no máximo 18 anos, ele fica parado na minha frente e diz Vamos lá para casa para você me chupar todinho fiquei paralisada e sem reação, ele logo foi embora, mas estragou o resto do meu dia e me fez perder toda a tranquilidade que estava ao aproveitar a praia.',22,FALSE,'05.04.2018','Rio de Janeiro','Rio de Janeiro',17,1,7,7,1,ST_GeomFromText('POINT(-43.3247 -23.0113)', 4326));
