INSERT INTO TBL_ASSEDIO VALUES (62,'22.04.2014','Neste local há um posto de gasolina em que os funcionários homens cometem constantemente assédio verbal contra qualquer mulher que passa desacompanhada de outro homem. Eles não possuem respeito algum pelas mulheres que ali passam, não há como desviar do posto atravessando a rua pois a prefeitura fez um bloqueio no meio da avenida, e mesmo que a avenida esteja movimentada de carros e pessoas na rua, eles não se sentem intimidados e agridem verbalmente as mulheres que ali passam. ',23,TRUE,'13.03.2018','São Bernardo do Campo','São Paulo',17,1,7,3,1,ST_GeomFromText('POINT(-46.5538 -23.6861)', 4326));
INSERT INTO TBL_ASSEDIO VALUES (63,'04.04.2014','Fui vítima de um assédio verbal comum: alguns homens se juntaram para assobiar, chamar de gostosa e falar coisas como vou te comer todinha e outros dizeres sexuais e comentários sobre meu corpo.',24,TRUE,'13.03.2018','Belo Horizonte','Minas Gerais',17,1,7,5,1,ST_GeomFromText('POINT(-43.9428 -19.9245)', 4326));
