INSERT INTO TBL_ASSEDIO VALUES (122,'05.04.2013','Caminhava em direção ao ponto de ônibus quando um ocupante de um caminhão começou a falar aquelas coisas nojentas enquanto eu passava Que isso, novinha! Vem cá! Assoviava, mandava beijos... Mandei ele ir se fuder e mandei o dedo do meio. Virei a esquina em direção à alameda pra seguir o meu caminho. Quando o sinal abriu e eles também foram para a alameda, eles fizeram questão de diminuir a velocidade e buzinar, provocar mais ainda e ficar me chamando e debochando cada vez que se aproximavam de mim. Depois o tráfego os forçou a tomar uma velocidade maior e seguir o caminho deles. Aquilo ferveu o meu sangue de uma tal forma que poucas vezes na vida me aconteceu...',19,TRUE,'01.02.2018','Niterói','Rio de Janeiro',17,2,7,3,1,ST_GeomFromText('POINT(-43.1013 -22.8803)', 4326));
