INSERT INTO TBL_ASSEDIO VALUES (285,'01.03.2014','Estava esperando um táxi em um ponto bem perto da minha casa. Era um sábado de Carnaval e estava chovendo. Passou um carro cheio de homens que gritaram para mim: minha nossa senhora, hein!. Eles pararam no sinal e eu atravessei a rua. Continuei esperando um táxi, fazendo sinal para os que passavam, mas nada. Menos de 1 minuto que estava ali, um cara em um carro cinza chegou mais perto, abriu a janela e perguntou: já pediu?. Me virei e saí de lá, fiquei atrás de uma banca de jornal com medo de ele vir atrás de mim. No final, ele foi embora com o carro, mas meu coração quase saiu pela boca.',24,FALSE,'07.08.2017','Belo Horizonte','Minas Gerais',17,2,7,2,1,ST_GeomFromText('POINT(-43.9357 -19.9336)', 4326));
