INSERT INTO TBL_ASSEDIO VALUES (347,'19.10.2012','Estava esperando um onibus no local e tomando um picolé, quando parou um carro cheio de homens e que começaram a mexer comigo. Não dei bola, mas cada vez q eu mordia o picolé eles faziam sons relacionados a sexo. Foi horrível, todos na rua olhando para mim, como se eu estivesse provocando os caras por estar comendo um picolé.',22,TRUE,'25.05.2017','São Paulo','São Paulo',17,2,11,7,6,ST_GeomFromText('POINT(-46.7115 -23.5726)', 4326));
