INSERT INTO TBL_ASSEDIO VALUES (409,'12.04.2011','Eu estava no ônibus UDESC - Semi-direto, no Terminal do Centro, quando um homem pediu licença pra sentar ao meu lado apesar de terem vários assentos sobrando. Até aí tudo bem. Fiquei distraída ouvindo música e olhando pela janela. Depois de 10 minutos olhei para o lado por acaso e percebi que o homem sentado ao meu lado estava com o pênis pra fora. Fiquei sem reação, só saí o mais rápido que pude do campo visual dele. Não gritei, não pedi ajuda. Na verdade, tudo aconteceu de uma forma tão inesperada que eu demorei pra entender. Compartilho a história para que mulheres entendam que isso é uma possibilidade real. Estejam preparadas, peçam ajuda e denunciem. Atos de desrespeito como este não podem ficar impunes. ',24,TRUE,'15.03.2017','false','Minas Gerais',18,2,8,3,1,ST_GeomFromText('POINT(-48.3811 -18.8977)', 4326));
