INSERT INTO TBL_ASSEDIO VALUES (485,'19.09.2012','Sou homem e de natureza tímida, mas dentro do trem lotado saindo de Santo André sentido Brás, o trem balançava com o movimento nos trilhos do velho espanhol, segurava minha mochila em uma mão e segurava-me com a outra. Em volta, a minha frente, 3 homens, mas apenas um rapaz de  frente carregando uma pasta. Como estava todo mundo apertado, não levei em conta o contato corpo-a-corpo com o rapaz, mas como sempre faço, tentei mudar de posição para não causar constrangimento, então descobri que o rapaz tocava sua mão propositadamente em meu pênis,neste instante que percebi, ele desceu imediatamente na parada da estação e ainda olhou pra trás com um sorriso sarcástico no rosto. Nunca tinha passado por isso. Não soube o que fazer.',49,TRUE,'03.01.2017','Santo André','São Paulo',16,1,11,7,2,ST_GeomFromText('POINT(-46.5287 -23.6521)', 4326));
