INSERT INTO TBL_ASSEDIO VALUES (504,'11.04.2014','Em frente ao Shopping Center 3, estava esperando para encontrar a minha irmã, quando um homem (aparentemente morador de rua, mas nitidamente lúcido) me parou, e perguntou se podia dividir algumas palavras comigo. Como isso acontece frequentemente ali, eu permiti. Ele começou a falar sobre uma outra vida em que tinha me conhecido e, quando eu achei que o papo seria religioso, veio para cima de mim com uma infinidade de obscenidades impronunciáveis. Gráficas, literais, e humilhantes. Eu só xinguei e virei as costas, sem saber o que mais poderia ter feito. ',23,TRUE,'23.12.2016','São Paulo','São Paulo',18,2,7,7,1,ST_GeomFromText('POINT(-46.6597 -23.5585)', 4326));
