INSERT INTO TBL_ASSEDIO VALUES (624,'21.08.2013','Estava indo para a aula do mestrado, eram aproximadamente 7h. No caminho para o ponto de ônibus, um homem passou por mim e disse Eu me enfiava todinho. Segui caminhando, tentando ignorá-lo, mas percebi que ele continuava me olhando. Foi, como sempre, horrível. Me senti suja o dia inteiro.',26,TRUE,'24.08.2016','Porto Alegre','Rio Grande do Sul',17,1,7,2,1,ST_GeomFromText('POINT(-51.2161 -30.0417)', 4326));
