INSERT INTO TBL_ASSEDIO VALUES (640,'05.05.2014','Voltando do trabalho para casa, um homem baixo e franzino lançava olhares para meu corpo. Ao me dirigir à porta para sair do metrô, o infeliz se coloca ao meu lado e passa a mão nos meus quadris. Saímos os dois no mesmo momento - e ele com o supercílio aberto, de tanta bolsada e soco na cara. Ninguém na plataforma interveio, nenhum segurança veio na minha direção, mas ele nunca mais esquecerá a pena por ter passado a mão na pessoa errada. Nesse tipo de crime, nenhuma medida judicial é tão eficiente quanto a autodefesa; o criminoso só age na certeza da impunidade, da fragilidade da vítima e pára de delinquir quando sabe que a punição é certa, que será efetivamente pego em flagrante. ',0,TRUE,'12.08.2016','São Paulo','São Paulo',16,2,7,2,1,ST_GeomFromText('POINT(-46.6338 -23.544)', 4326));
INSERT INTO TBL_ASSEDIO VALUES (641,'05.05.2014','Fui perseguida por um carro  durante vários dia toda vez que ia para o trabalho e sempre neste mesmo local, fazia cara feia ,xingava, nada adiantou ele debochava dos meus xingamentos, até que anotei a  placa do carro e fui na delegacia, ele foi localizado e não apareceu no dia da audiência, mais parou de me perseguir.',34,TRUE,'12.08.2016','Rio de Janeiro','Rio de Janeiro',17,1,7,2,2,ST_GeomFromText('POINT(-43.3329 -22.9377)', 4326));
