INSERT INTO TBL_ASSEDIO VALUES (644,'29.04.2014','Um home negro em um carro preto, assedia mulheres, e as segue pelas redondezas da Vila Ré e Jd Popular, ele fica chamando e querendo dar carona, ele trabalha em uma loja de manutenção de carros na av Itinguçu mesmo pois já o vi lá. Da próxima vez, fui orientada pelo meu marido a chamar a policia. Isso é uma vergonha. Tenho medo...',26,FALSE,'04.08.2016','São Paulo','São Paulo',17,2,11,0,2,ST_GeomFromText('POINT(-46.502 -23.5272)', 4326));
