INSERT INTO TBL_ASSEDIO VALUES (678,'05.07.2012','Estava voltando do trabalho, por volta das 13:30, esse horário na linha do trólebus para Ferrazópolis é lotado. Um homem se aproximou e sem eu perceber estava atrás de mim, como estava cheio não percebi, percebi depois de alguns minutos que ele estava apertando o corpo contra a lateral do meu, quando percebi tentei sair de perto, e ele colocou a mão na genitália e tentou se aproximar, fui para o outro lado do ônibus, ele me seguiu e ficava com a mão na genitália me olhando. Desci no meu ponto, ele desceu e me seguiu, desviei o caminho e aperei o passo, não o vi mais. Passei muito medo neste dia. Havia acabado de me mudar para São Paulo, quando fiquei sabendo do site entrei na hora, precisamos denunciar. ',28,TRUE,'12.07.2016','Diadema','São Paulo',16,2,11,7,6,ST_GeomFromText('POINT(-46.5832 -23.6917)', 4326));
