INSERT INTO TBL_ASSEDIO VALUES (683,'25.10.2013','Por volta das 19:00 eu e minha amiga saimos do salão de beleza e fomos para o ponto de onibus ela iria no onibus e eu estava a acompanhando esperando meu pai chegar para me levar para casa. Um homem em uma moto preta parou e perguntou as horas eu respondi meio encanada sem dar moral, em seguida o onibus parou, me despedi da minha amiga e ela entrou, o homem ficou parado ali quando olhei estava com seu orgão genital para fora, entrei em um desespero e corri para dentro do onibus desesperada e chorando. Foi tudo tão rapido. O homem acelerou a moto e saiu correndo, o motorista queria segui-lo para pegar a placa mas eu nao conseguia nem falar, ate que falei ja era tarde.É um local sem nenhuma iluminação publica e um lote vago. Fiquei em estado de choque , pois eu iria ficar ali sozinha se o onibus ja tivesse saido do ponto.',16,TRUE,'05.07.2016','São José do Rio Preto','São Paulo',15,3,11,7,6,ST_GeomFromText('POINT(-49.3686 -20.7855)', 4326));
