INSERT INTO TBL_ASSEDIO VALUES (752,'05.05.2014','Assédio Verbal, stalking. Aconteceu, pq eu não sou turista na cidade e conheço as leis que deveriam ser cumpridas dentro de um ambiente que é designado para Proteção Ambiental. Meu pai estava próximo, mas o individuo não sabia, e eu ouvi coisas que foram bastante ofensivas, mas pelo simples fato dele ser guarda talvez achasse q tava no direito de falar certas coisas, tais como: é só dá uns tapinhas na bunda que resolve  essa palavras estavam sendo ditas para outro homem - daí eu me afastei. ',32,TRUE,'31.05.2016','false','Espírito Santo',17,1,11,0,1,ST_GeomFromText('POINT(-40.4735 -20.6544)', 4326));
