INSERT INTO TBL_ASSEDIO VALUES (769,'09.05.2014','Eu estava agora a pouco no McDonalds com algumas colegas do trabalho, estava bastante cheio, eu estava de saia, quando peguei meu lanche e subia as escadas eu ouvi um urro muito alto que todos do Mc também devem ter ouvidos, olhei para trás e havia um gordo negro vibrando.  Achei extremamente ofensivo, como se o cara nunca tivesse visto um par de pernas, sem falar na vergonha que passei.',32,TRUE,'23.05.2016','São Paulo','São Paulo',17,2,11,0,3,ST_GeomFromText('POINT(-46.6777 -23.5641)', 4326));
