INSERT INTO TBL_ASSEDIO VALUES (771,'06.12.2013','Sou acadêmica de medicina na cidade de Teresópolis e tive o bumbum fotografado dentro da sala de aula e exposta na internet por outros alunos do curso de medicina. Além disso, foram feitos comentários nojentos sobre o meu corpo. Estou muito indignada com a postura de alunos que, se espera, que sejam pessoas de uma educação, cultura e respeito acima do comum, pois a profissão que será exercida pelos mesmos jamais permitiria um comportamento tão sujo, tão podre, tão absurdo.',29,TRUE,'20.05.2016','Teresópolis','Rio de Janeiro',16,0,7,7,6,ST_GeomFromText('POINT(-42.9755 -22.417)', 4326));
INSERT INTO TBL_ASSEDIO VALUES (772,'17.10.2013','Eu estava indo ver meu namorado, a rua estava meio vazia. Esse cara estava num carro vermelho, já o vi umas três vezes nesse mesmo local. Ele fez quatro convites afim de me convencer a entrar em seu carro, vendo que não ia rolar, cuspiu em mim e saiu me xingando. To deixando o alerta porque a rua é meio vazia e tem um bar ali próximo onde presenciaram tudo mas ficaram quietos. O bar só tinha homens.',37,TRUE,'20.05.2016','São José dos Campos','São Paulo',28,2,10,7,1,ST_GeomFromText('POINT(-45.8882 -23.2246)', 4326));
