INSERT INTO TBL_ASSEDIO VALUES (855,'27.05.2013','Estava andando pela Herval do centro para a UEM, quase na altura do estádio, quando notei que duas meninas bem mais a minha frente andavam de braços dados bem rapidamente. Tinha um cara, de uns 40 anos, seguindo elas e falando coisas que não consegui ouvir. Detalhe: elas deveriam ter no MÁXIMO 12 anos de idade. Elas olhavam pra trás aflitas e quase correndo entraram numa lanchonete, e só aí o cara saiu (gargalhando e tranquilamente) em direção de uma obra que estava ocorrendo no Willie Davids, onde entrou e deveria trabalhar.',0,TRUE,'01.04.2016','Maringá','Paraná',23,2,11,0,1,ST_GeomFromText('POINT(-51.9364 -23.4161)', 4326));
