INSERT INTO TBL_ASSEDIO VALUES (860,'02.05.2014','Tem uma loja em que vende apartamentos e tinha dois homens no telhado arrumando alguns cabos, provavelmente tecnicos, quando eu passeium deles ficou assobiando para mim, e como eu normalmente sempre revido cantadas de rua, perguntei qual era o problema dele e que era para ele me deixar em paz ou eu falaria com o chefe dele ou responsavel.O cara chegou a ficar branco de surpresa e tentou esconder a cara. Quando eu falei oque aconteceu para as minhas amigas e a minha mae elas acharam que eu sou uma maluca por ter revidado uma resposta dessa.',0,TRUE,'28.03.2016','Goiânia','Goiás',17,2,6,7,6,ST_GeomFromText('POINT(-49.2558 -16.7)', 4326));
