INSERT INTO TBL_ASSEDIO VALUES (873,'04.06.2014','Homem que trabalha numa loja de produtos orientais, próximo ao sesc tijuca e de um lava jato, sempre que mulheres passam pela loja ele solta comentários agressivos sobre nós, se referindo a partes do corpo, sem se preocupar com idade... Os olhares q ele tem sobre o alvo é repugnante e sua atitude é diária!',17,TRUE,'15.03.2016','Rio de Janeiro','Rio de Janeiro',17,2,11,0,6,ST_GeomFromText('POINT(-43.245 -22.9254)', 4326));
