INSERT INTO TBL_ASSEDIO VALUES (879,'13.06.2002','Um grupo de garotos brancos de classe média me cercaram e um deles tentou me beijar a força. Eu reagi empurrando a face dele para longe e tentei um joelhada que não acertou. Quando sai ele me puxou pelo cabelo, voltei e empurrei ele. Ele tentou me bater, mas os amigos dele o puxaram e saíram correndo.',0,TRUE,'11.03.2016','Belo Horizonte','Minas Gerais',16,2,11,7,6,ST_GeomFromText('POINT(-43.9229 -19.9523)', 4326));
