INSERT INTO TBL_ASSEDIO VALUES (907,'03.06.2014','Foi horrivel. Ela me chamou de gostoso, safado no meio de todo mundo, e que eu marcasse bobeira, me agarrava na frente de todo mundo. Me chamou de cachorro sem vergonha e deu um tapa na minha bunda. Nunca me senti tão envergonhado em toda a minha vida. ',0,TRUE,'29.02.2016','Vitória','Espírito Santo',17,3,11,7,1,ST_GeomFromText('POINT(-40.3465 -20.2892)', 4326));
