INSERT INTO TBL_ASSEDIO VALUES (1049,'29.07.2014','Estava caminhando para ir trabalhar e um marginal passou de moto perguntando se eu era Lésbica - provavelmente ele já sabia - e disse que ia me pegar e me estuprar para que eu gostasse de homem. Um absurdo e extremamente assustador, fiquei apavorada. Se vc denuncia ainda não acontece nada já que a policia para agir espera que aconteça algo. Lamentável.',29,TRUE,'07.12.2015','Ilhéus','Bahia',21,1,7,2,6,ST_GeomFromText('POINT(-39.0493 -14.7993)', 4326));
