INSERT INTO TBL_ASSEDIO VALUES (1183,'31.10.2014','Minha amiga e eu estávamos retornando da escola. Descemos do ônibus e caminhamos em direção ao segundo ônibus, quando um homem adulto, trabalhador de uma madeireira, ao descarregar um caminhão nos cumprimentou: Boa tarde, meninas bonitas. Constrangidas, nós duas tentamos continuar com cara de paisagem até manter certa distância do local. É importante mencionar que eu tenho 17 anos e ela 16, ambas estávamos de camisa polo do uniforme, calça jeans (não muito skinny), tênis e cabelo preso em coque.',17,TRUE,'18.11.2015','Rio de Janeiro','Rio de Janeiro',17,2,11,0,2,ST_GeomFromText('POINT(-43.5599 -22.8949)', 4326));
