INSERT INTO TBL_ASSEDIO VALUES (1257,'01.11.2014','Estava andando na praia de camisa larga e legging preto, e um senhor idoso passou a mão na minha perna. Foi tão rápido que fiquei em choque e não tive reação. Mas fiquei tão chateada que penso nisso toda hora que saio de casa para caminhar no mesmo trecho da praia.',27,TRUE,'02.11.2015','Rio de Janeiro','Rio de Janeiro',16,1,9,3,1,ST_GeomFromText('POINT(-43.2216 -22.9867)', 4326));
