INSERT INTO TBL_ASSEDIO VALUES (1401,'30.11.2014','Olá meu nome é Erika, eu tenho 16 anos e sofro por assédio sexual toda vez que saio. São aqueles nojentos falando gostosa, nossa com essa eu caso em e todos os demais. Odeio todos os comentários que ouço na rua. Minha irmã tem 13 anos e sofre assédio também, pelo corpo que ela tem minha mãe diz que ela não pode usar as roupas que ela usa por ser chamativa. Mas ela gosta vê as meninas da idade dela usando, e não vê problema nenhum nisso. Não é a roupa que estrupa, e sim o estuprador. ',16,TRUE,'19.10.2015','Campinas','São Paulo',17,2,4,7,4,ST_GeomFromText('POINT(-47.0323 -22.85)', 4326));
