INSERT INTO TBL_ASSEDIO VALUES (1437,'02.08.1997','Eu era criança, estava na esquina da casa dos meus avós esperando minha mãe chegar, quando encostou um carro com um senhor dentro. Ele abaixou o vidro e disse que queria comer minha bucetinha. Eu entendi o que ele havia dito, mas travei, fique parada enquanto ele dizia outras coisas que nem fui capaz de absorver, até que meu avô chegou e o mandou embora aos gritos e ameaças. Esse foi o primeiro episódio, e 17 anos depois, de contar sinto um arrepio na alma, algo que não sara.',11,TRUE,'01.10.2015','Santo André','São Paulo',17,1,4,7,1,ST_GeomFromText('POINT(-46.5179 -23.667)', 4326));
