INSERT INTO TBL_ASSEDIO VALUES (1450,'10.09.2005','Eu tinha cerca de 14 anos de idade, me vestia como uma criança....e nesse dia encontrei com o homem que entregava gás em casa, com o qual sempre fui muito gentil e sempre tratava educadamente. Nesse dia, quando voltava da escola, no horário do almoço com a rua deserta, ele eme chamou e quando virei para responder ele estava como pau para fora se masturbando em plena luz do dia. Fiquei em pânico e corri para casa, hoje eu ainda   vejo pela rua nas redondezas do ocorrido. ',25,TRUE,'29.09.2015','Belém','Pará',18,1,8,2,6,ST_GeomFromText('POINT(-48.501 -1.46514)', 4326));
