INSERT INTO TBL_ASSEDIO VALUES (1457,'10.12.2014','Estava a caminho da estação de metrô quando um homem que passava ao meu lado disse a palavra: delícia eu fiquei nervosa e perguntei a ele se ele nunca viu mulher na vida dele e ele imediatamente me xingou de puta, vaca que era louca, que ele não me disse nada... em fim foi um escândalo no meio da rua, ele me seguiu ate o metro e me ameaçou colocando o dedo na minha cara dizendo que eu não sabia com quem estava mexendo... ',29,FALSE,'28.09.2015','São Paulo','São Paulo',17,1,11,7,1,ST_GeomFromText('POINT(-46.6632 -23.5552)', 4326));
