INSERT INTO TBL_ASSEDIO VALUES (1487,'12.12.2014','Estava no DoSol e um homem mais velho, com mais de 40 anos, estava lá e quando cheguei ele me cumprimentou. Quando a banda acabou que fui sair para ir pra rua, percebi q ele estava tentando me chamar/tocar em mim para me puxar, mas me afastei. Ele começou a GRITAR no meio do DoSol que eu era muito linda. Fiquei paralisada e minha amiga me puxou pra sairmos de lá. Mas me senti péssima, só relaxei quando vi que ele tava indo embora. Comentei com outra amiga depois e ela disse que ele também puxou papo com ela.',21,FALSE,'21.09.2015','Natal','Rio Grande do Norte',17,3,11,7,6,ST_GeomFromText('POINT(-35.2056 -5.7751)', 4326));
