INSERT INTO TBL_ASSEDIO VALUES (1523,'18.04.2012','Estava voltando do trabalho para casa à tarde. Passei por um grupo de homens fazendo um carregamento em frente a um depósito de material de construção. Um deles disse bem no meu ouvido que linda e eu já sem paciência para cantadas de rua mostrei o dedo do meio. Ele começou a me xingar e xingar todas as mulheres da minha família, desferir diversas obscenidades e eu fiquei com ainda mais raiva, virei e mostrei os dois dedos. Ele ficou furioso e vei andando atrás de mim. Fiquei com medo de apanhar e corri. Ele desistiu e ficou ouvindo deboche dos outros homens que estavam com ele.',28,TRUE,'10.09.2015','São Paulo','São Paulo',17,2,7,7,2,ST_GeomFromText('POINT(-46.646 -23.6466)', 4326));
