INSERT INTO TBL_ASSEDIO VALUES (1679,'08.03.2015','Caminhando com a minha amiga pelo Batel (bairro de Curitiba), vários homens que por nós passaram falaram coisas desrespeitosas; eu não sabia o que fazer ou como reagir, estava muito desconfortável...Decide ignorar o assédio, continuar caminhando como se não tivesse ouvido nada. Será que deveria ter mandado eles calarem a boca? Se bem que eu tenho medo. Acho horrível me sentir assim, mas é verdade. Tenho medo dos homens.',18,FALSE,'16.08.2015','Curitiba','Paraná',17,2,7,7,1,ST_GeomFromText('POINT(-49.2673 -25.429)', 4326));
INSERT INTO TBL_ASSEDIO VALUES (1680,'08.03.2015','No Dia Internacional das Mulheres, o Shopping Metropolitano fez uma campanha para homenagear as mulheres. E uma das peças publicitárias, adesivando os espelhos dos banheiros femininos, trazia a inscrição Fiu Fui, que é isso hein?. ',32,FALSE,'16.08.2015','Rio de Janeiro','Rio de Janeiro',27,3,11,0,2,ST_GeomFromText('POINT(-43.3733 -22.972)', 4326));
