INSERT INTO TBL_ASSEDIO VALUES (1755,'07.04.2015','VOCÊ GOSTA DE SER ESTUPRADA? foi o que um homem gritou de dentro do carro depois que eu revidei, com um gesto, o assédio dele. Me senti calada... como se eu não pudesse reagir nunca a uma agressão verbal de um homem na rua. Voltei imediatamente para casa e desisti de fazer o que tinha que fazer por medo de andar pela rua e ser estuprada. ',23,TRUE,'02.08.2015','Niterói','Rio de Janeiro',15,1,7,7,1,ST_GeomFromText('POINT(-43.1012 -22.8598)', 4326));
INSERT INTO TBL_ASSEDIO VALUES (1756,'27.03.2015','Eu estava mechendo no PC da lan house da minha mãe, quando um conhecido nosso de uns 19 anos +- chamado Douglas pressionou uma cadeira de plastico contra minha bunda no momento que meus pais estavam lá fora, e eu olhei ele com raiva, além disso ele vivia dizendo que ia casa comigo, e eu dizia que não queria, ele não era o tipo de pessoa que eu gosto, fazia muitos comentários maldosos',17,FALSE,'02.08.2015','Votorantim','São Paulo',16,2,6,7,1,ST_GeomFromText('POINT(-47.4544 -23.5636)', 4326));
