INSERT INTO TBL_ASSEDIO VALUES (1763,'08.04.2015','Estava caminhando para o serviço quando um caminhão, pertencente a oficina que funciona no final da rua, estava fazendo o retorno, o passageiro colocou metade do corpo do lado de fora do caminhão para ficar me olhando.',25,TRUE,'01.08.2015','Manaus','Amazonas',27,1,8,2,6,ST_GeomFromText('POINT(-59.9887 -3.09623)', 4326));
