INSERT INTO TBL_ASSEDIO VALUES (2096,'23.07.2015','Todos os dias a mesma situação, sou obrigada a me privar de usar roupas que gostaria por causa das cantadas inevitáveis que levarei ao botar o pé na calçada. Não é possível mais me sentir segura em qualquer lugar que eu ande sozinha. Estava indo para a casa de uma amiga e como sempre foi uma missão conseguir pegar um ônibus sem levar uma cantada ou uma encarada daquelas que te devoram. Me sinto repremida, sem ter como me defender. Sempre que saio de short amarro um casaco na cintura como forma de me sentir menos violada. ',18,TRUE,'09.05.2015','Rio de Janeiro','Rio de Janeiro',17,2,7,2,1,ST_GeomFromText('POINT(-43.361 -22.918)', 4326));
INSERT INTO TBL_ASSEDIO VALUES (2097,'19.05.2009',' Um amigo da família foi me dar uma carona porque eu estava passando mal e aproveitou para me estuprar, ele me levou para um lugar deserto, eu consegui correr e pedi ajuda, gritei, mas ninguém me socorreu, então ele me alcançou e me arrastou até uma plantação de cansançao e me estuprou.',17,TRUE,'08.05.2015','Cícero Dantas','Bahia',19,3,6,7,1,ST_GeomFromText('POINT(-38.3797 -10.5905)', 4326));
