INSERT INTO TBL_ASSEDIO VALUES (2142,'25.05.2015','Estava voltando da aula, de calça jeans, blusa e casaco. O porteiro de um prédio conversava com um cara na calçada e, quando eu passei, ele me encarou. Olhando fixo para o meu rosto, ele falou o que você faz quando uma moça bonita e indefesa passa assim?. Acelerei o passo e não quis ouvir o resto, mas fiquei muito nervosa e com raiva. Ficou claro para mim que era uma ameaça, especialmente pela palavra indefesa. Me arrependi muito de não ter reagido, mas como passo por ali todos os dias, tive medo.',22,TRUE,'22.04.2015','Rio de Janeiro','Rio de Janeiro',17,1,7,1,1,ST_GeomFromText('POINT(-43.2216 -22.9836)', 4326));
INSERT INTO TBL_ASSEDIO VALUES (2143,'01.07.2015','Um grupo de homens ficam próximos ao Largo do Verdun, esperando meninas saírem do colégio e dizem palavras de baixo calão quando as mesmas passam. Inclusive já escutei absurdo saindo da escola ',17,TRUE,'22.04.2015','Rio de Janeiro','Rio de Janeiro',17,2,6,7,3,ST_GeomFromText('POINT(-43.2616 -22.9195)', 4326));
