INSERT INTO TBL_ASSEDIO VALUES (2194,'01.08.2015','Estava andando a caminho de casa e um tres homens dentro de um caminhao pararam e ficaram me perguntando em tom de malícia se ele estava bonito, perguntou varias vezes e eu nao dei atenção. Entao ouvi um xingamento bastante ofensivo, me rebaixando como mulher e ainda fui chamada de metida por nao responder.',16,TRUE,'10.04.2015','Piracicaba','São Paulo',17,2,11,0,1,ST_GeomFromText('POINT(-47.6334 -22.7515)', 4326));
INSERT INTO TBL_ASSEDIO VALUES (2195,'01.08.2015','Estava caminhando e um carro passou e o homem dentro do carro saiu pra fora da janela e me olho como se eu fosse um pedaço de carne, ede maneira bem ofensiva me dirigiu uma palavra de baixo calão, fiquei muito brava e meio com medo de ele voltar e fazer algo.',0,TRUE,'10.04.2015','Piracicaba','São Paulo',17,2,11,0,6,ST_GeomFromText('POINT(-47.6328 -22.7489)', 4326));
