INSERT INTO TBL_ASSEDIO VALUES (2229,'04.08.2015','Um homem de bicicleta me abordou por volta de 10hrs da manhã e disse baixinho que gostaria de me ver nua, na cama dele. Feito isso, seguiu em frente por mais uns metros e para minha infelicidade, retornou e disse mais umas coisas que, para minha felicidade, não consegui entender. Ele tinha uns 30 anos aproximadamente, era magro e moreno. Usava uma camisa regata azul claro e sua bicicleta era vermelha, dessas que entregadores de farmácia usam. ',24,TRUE,'22.03.2015','Rio das Ostras','Rio de Janeiro',17,1,11,7,6,ST_GeomFromText('POINT(-41.9816 -22.5498)', 4326));
