INSERT INTO TBL_ASSEDIO VALUES (2303,'22.08.2015','Normalmente, fazia a pé o trajeto para a academia, por morar perto, mas sempre escutava cantadas, piadinhas e palavras nojentas. Mas um dia especificamente fui seguida por um sujeito que parecia um morador de rua. Ele me seguiu por dois quarteirões, falando do meu corpo e do que ia fazer comigo, de um jeito tão asqueroso que eu sequer tenho coragem de reproduzir aqui. Entrei no primeiro restaurante que vi e fiquei muito abalada. Chorei quando cheguei em casa e, depois disso, nunca mais fui à academia a pé',23,TRUE,'18.02.2015','Fortaleza','Ceará',17,1,7,7,1,ST_GeomFromText('POINT(-38.5103 -3.74541)', 4326));
