INSERT INTO TBL_ASSEDIO VALUES (2309,'23.08.2015','Hoje de manhã, um domingo, 10 horas, sai pra tomar café numa padaria; usava uma calça com estampa de oncinha. Estava sozinha. Caminhava pela Borges Lagoa e vi de longe dois homens que conversavam na frente dessa padaria Lamel. Pensei em atravessar a rua, pensei em ligar a câmera do celular, mas afinal decidi abaixar a cabeça e continuar caminhando. Não deu outra: foi só eu passar que os caras mexeram comigo, me chamando de safada  e dizendo que gostariam de ter essa onça na cama; Me deu muita vontade de responder, mas fiquei com medo, aumentei meu passo e na hora de voltar pra casa tomei outro caminho.',25,TRUE,'13.02.2015','São Paulo','São Paulo',17,1,8,2,1,ST_GeomFromText('POINT(-46.6475 -23.5966)', 4326));
