INSERT INTO TBL_ASSEDIO VALUES (2471,'20.09.2015','Ao descer do ônibus ontem (20) com minha amiga, um homem que estava nele ficou de pé e colocou a cabeça para fora de janela só para assobiar para nós e nos olhou de um jeito muito constrangedor. Ele aparenta ter de 20 a 30 anos.',17,TRUE,'15.12.2014','Itajubá','Minas Gerais',17,3,11,0,1,ST_GeomFromText('POINT(-45.495 -22.4319)', 4326));
