INSERT INTO TBL_ASSEDIO VALUES (2636,'22.10.2015','No meu caminho para um exame médico, passei por dois homens que atravessavam a rua. Um deles sem camisa e encostando em mim e o outro com a mão no saco, me olhando e no momento que cruzei com ele mordeu os lábios fez o famoso som de desejo que conhecemos. Xinguei e os dois riram. :/',24,FALSE,'21.11.2014','Araraquara','São Paulo',17,2,11,7,1,ST_GeomFromText('POINT(-48.1839 -21.7897)', 4326));
