INSERT INTO TBL_ASSEDIO VALUES (2652,'27.10.2015','Estava pedalando por volta das 8h30 da manhã quando um senhor de idade que caminhava pelo bosque parou de andar, ficou me observando e quando passei ele soltou gostosa, hein.',25,TRUE,'20.11.2014','Goiânia','Goiás',17,1,11,7,6,ST_GeomFromText('POINT(-49.2615 -16.6841)', 4326));
