INSERT INTO TBL_ASSEDIO VALUES (2835,'10.12.1978','Eu era pré adolescente, 11/12 anos. Uma das minhas primeiras saídas na rua. Sai para ir na padaria, distante a duas quadras da minha casa. Usava um blusa sem mangas. Meus seios começavam a despontar. Um cara passou e com cara de desejo falou algo nojento sobre meus seios - agora não me recordo que palavra ele usou. Sei que o sentimento foi de medo, repulsa e vontade de desaparecer.',49,TRUE,'25.09.2014','Rio de Janeiro','Rio de Janeiro',17,2,8,5,1,ST_GeomFromText('POINT(-43.2048 -22.9838)', 4326));
