INSERT INTO TBL_ASSEDIO VALUES (2839,'25.11.2015','Foi na minha escola, um  menino passou a mão na bunda de uma menina na sala de aula, a professora não o tirou da sala, mas os colegas se indignaram. A direção tratou como nada. Tenho medo de quantas coisas parecidas já devem ter acontecido e abafadas.',0,TRUE,'22.09.2014','São Leopoldo','Rio Grande do Sul',28,0,11,0,6,ST_GeomFromText('POINT(-51.129 -29.7723)', 4326));
INSERT INTO TBL_ASSEDIO VALUES (2840,'01.12.2015','Infelizmente hoje em dia não temos a liberdade nem para praticar um esporte ao ar livre. Não eram nem oito horas da manhã, eu voltava para casa depois de uma caminhada e de outra calçada podia ouvir os assobios e os gritos Ei Gostosa! . Eu nunca gostei disso,pra mim isso não é elogio,isso me deixa envergonhada. Continuei caminhando e logo mais a frente os dois rapazes que estavam assobiando chegaram perto de mim,eu explodi!Não aguentei,xinguei os dois,pedi para que me respeitassem pois aquele comportamento me constrangia. Eles acharam ruim de eu reclamar,acaharm que eles estavam certos e eu a errada,diziam que eram só elogios!... O que mais me decepciona é saber que existem muitas mulheres que gostam desses tipos de elogios e isso da a liberdade para esses imbecis praticarem tal ato! Eu não estava com roupa curta,vestia uma calça legging comprida e uma blusa amarrada na cintura e camiseta fechada . Pra mim chega, chamar de gostosa não é elogio!',25,TRUE,'21.09.2014','Mogi das Cruzes','São Paulo',17,1,6,2,1,ST_GeomFromText('POINT(-46.1905 -23.5669)', 4326));
