INSERT INTO TBL_ASSEDIO VALUES (2852,'06.12.2015','Estava voltando para casa, quando um homem que estava na minha frente olhou p trás, parou e ficou me encarando quando passei por ele, ele falou uma coisa seguido de uma respiração nojenta. Gritei se ele tinha perdido a noção. Ninguém fez nada, ele começou a gritar que eu era louca!',23,FALSE,'03.09.2014','Porto Alegre','Rio Grande do Sul',17,2,7,1,1,ST_GeomFromText('POINT(-51.2192 -30.0281)', 4326));
INSERT INTO TBL_ASSEDIO VALUES (2853,'03.12.2015','Eu estava no metrô, e do meu lado havia outra mulher sentada. Um rapaz chegou na frente dele, e perguntou Sua mãe não te ensinou nada. Com um decote desses a essa hora da manhã? É puta mesmo. Ela começou a chorar. E ele simplesmente ficou ali. Até chegar sua hora de descer. E ele saiu. COmo se nada tivesse acontecido.',0,TRUE,'02.09.2014','Rio de Janeiro','Rio de Janeiro',28,1,11,0,6,ST_GeomFromText('POINT(-43.1763 -22.9079)', 4326));
