INSERT INTO TBL_ASSEDIO VALUES (2891,'28.11.2015','Vendo hoje o programa ESQUENTA me inspirou mutio vir até aqui para comunicar , é algo grave que deve se ter alguma consequência . O que cometeu o assedio é meu professor de curso , com o ocorrido fiquei sem ter vontade de sair de casa com medo de algo , ele me mandou diversas mensagens foi ai que comuniquei meus pais , que foram até a coordenação da escola . Mas ainda não sabemos o que será feito. Agradeço desde já !',16,FALSE,'10.08.2014','Viamão','Rio Grande do Sul',28,0,11,0,2,ST_GeomFromText('POINT(-51.0202 -30.0826)', 4326));
