INSERT INTO TBL_ASSEDIO VALUES (2924,'10.10.2015','Todos os dias passo em frente ao estacionamento de carga e descarga do hipermercado Condor. Todos os dias sou assediada verbalmente por motoristas e funcionários que ali estão, seja com o próprio fiu fiu, palavras de baixo calão e comentários desprezíveis. Já vi também outras moças passarem pela mesma situação no local. ',19,TRUE,'15.07.2014','Paranaguá','Paraná',17,1,6,7,1,ST_GeomFromText('POINT(-48.5177 -25.5188)', 4326));
