INSERT INTO TBL_ASSEDIO VALUES (2968,'12.01.2016','Estava me despedindo da minha mãe no ônibus que já ia partir quando chegou um vendedor de cartões com frases bíblicas. Ele entregou os cartões para os passageiros. Eu não estava acostumada com aquilo, então me esquivei um pouco, o que o fez  entregar o cartão no meu colo, bem perto das minhas partes íntimas. Tudo bem. Ele começou a recolher os cartões de todos. Devolvi o meu e saí. Quando passei pelo cara, ele me imprensou e esfregou a virilha na minha bunda. Fiquei constrangida e não contei a ninguém na hora. Só meses depois reportei o episódio a minha mãe e ela me repreendeu por ter ficado calada.',18,TRUE,'30.06.2014','Fortaleza','Ceará',16,2,6,5,1,ST_GeomFromText('POINT(-38.5969 -3.73937)', 4326));
