INSERT INTO TBL_ASSEDIO VALUES (3184,'22.05.2016','Estava indo à padaria com meu cachorro e uma amiga, e quando íamos atravessar a rua, abaixei pra colocar a guia no cachorro, quando um homem atravessou a rua e ficou me olhando por trás, me senti desconfortável com a presença dele, e minha amiga que estava indo na frente, resolveu voltar e esperar eu terminar de colocar a coleira no cachorro. Ele ainda ficou olhando um pouco, e depois seguiu em direção às casas, parou no beco e ficou olhando pra nós. Eu fiquei de fora da padaria, e quando ele percebeu que eu estava olhando pra ele, ao invés de seguir em frente, voltou por onde veio. Fiquei realmente assustada, porque a atitude dele foi muito suspeita.',0,TRUE,'09.05.2014','Brasília','Distrito Federal',27,2,11,0,6,ST_GeomFromText('POINT(-47.9001 -15.745)', 4326));
